import React, { useState, useEffect } from "react";
import { Card, Row, Col, Select, Layout, Image } from "antd";
import { Link } from "react-router-dom";
import axios from "axios";

type paramsType = {
	sorting: string;
};

export type companyType = {
	id?: string;
	company_name?: string;
	description?: string;
	employment_title?: string;
	location?: string;
	keywords?: Array<string>;
	rating?: number;
	upload_date?: string;
	end_date?: string;
	image?: string;
};

const RecommandCompany: React.FC = () => {
	const { Content } = Layout;
	const [params, setParams] = useState<paramsType>({ sorting: "New" });
	const [companies, setCompanies] = useState<Array<companyType>>([]);
	useEffect(() => {
		const fetchData = async () => {
			try {
				const response = await axios.get("/companies/", {
					params: params,
				});
				setCompanies(response.data.companies);
			} catch (err) {
				console.log(err);
			}
		};
		fetchData();
	}, [params]);
	return (
		<Content
			style={{
				margin: "0 auto",
				marginTop: "40px",
			}}
		>
			<div
				style={{
					display: "flex",
					justifyContent: "space-between",
					alignItems: "center",
					marginBottom: "20px",
				}}
			>
				<b style={{ fontSize: "18px" }}>추천 포지션</b>
				<Select
					defaultValue="new"
					onChange={(value: string) => setParams({ sorting: value })}
					options={[
						{ value: "new", label: "최신순" },
						{ value: "recommend", label: "추천순" },
						{ value: "end", label: "마감순" },
					]}
				/>
			</div>
			<Row
				gutter={[24, 24]}
				justify="space-around"
			>
				{companies.map((company) => {
					return (
						<Col
							span={6}
							key={company.id}
						>
							<Link
								to={`/company/id=${company.id}`}
								state={{ company: company }}
							>
								<Card
									hoverable
									cover={
										<Image
											src={company.image}
											preview={false}
											fallback="data:image/png;base64,"
										/>
									}
									style={{ width: "240px" }}
								>
									<Card.Meta
										title={company.company_name}
										description={company.description}
									/>
								</Card>
							</Link>
						</Col>
					);
				})}
			</Row>
		</Content>
	);
};

export default RecommandCompany;
