import React, { useState, useEffect } from "react";
import { Card, Row, Col, Select, Image, Layout } from "antd";
import { Link } from "react-router-dom";
import axios from "axios";

type paramsType = {
	sorting: string;
};

export type RecruitType = {
	id: string;
	company_name: string;
	description: string;
	employment_title?: string;
	location: string;
	keywords: Array<string>;
	rating: number;
	upload_date: string;
	end_date: string;
	images: Array<string>;
};

const RecuitCompanyPage: React.FC = () => {
	const [params, setParams] = useState<paramsType>({ sorting: "New" });
	const [recruits, setRecruit] = useState<Array<RecruitType>>([]);
	const { Content } = Layout;
	useEffect(() => {
		const fetchData = async () => {
			try {
				const response = await axios.get("/recruit/", {
					params: params,
				});
				if (params.sorting === "New") {
					setRecruit(response.data.recruit.reverse());
				} else {
					response.data.recruit.sort(function (
						a: RecruitType,
						b: RecruitType,
					) {
						const endDateA = a.end_date.split("-");
						const endDateB = b.end_date.split("-");
						if (Number(endDateA[0]) > Number(endDateB[0])) {
							return 1;
						} else if (Number(endDateA[0]) === Number(endDateB[0])) {
							if (Number(endDateA[1]) > Number(endDateB[1])) {
								return 1;
							} else if (Number(endDateA[1]) === Number(endDateB[1])) {
								if (Number(endDateA[2]) > Number(endDateB[2])) {
									return 1;
								} else {
									return -1;
								}
							} else {
								return -1;
							}
						} else {
							return -1;
						}
					});
				}
				setRecruit(response.data.recruit);
			} catch (err) {
				console.log(err);
			}
		};
		fetchData();
	}, [params]);
	return (
		<Content style={{ paddingTop: "80px", marginInline: "20%" }}>
			<Row>
				<Col span={24}>
					<div
						style={{
							display: "flex",
							justifyContent: "space-between",
							alignItems: "center",
							marginBottom: "20px",
						}}
					>
						<b style={{ fontSize: "18px" }}>추천 포지션</b>
						<Select
							defaultValue="new"
							onChange={(value: string) => setParams({ sorting: value })}
							options={[
								{ value: "new", label: "최신순" },
								{ value: "end", label: "마감순" },
							]}
						/>
					</div>
					<Row gutter={[12, 12]}>
						{recruits.map((recruit) => {
							return (
								<Col
									key={recruit.id}
									xs={24}
									md={12}
									xl={8}
									xxl={6}
								>
									<Link
										to={`/company/id=${recruit.id}`}
										state={{ recruit: recruit }}
									>
										<Card
											hoverable
											cover={
												<Image
													src={recruit.images[0]}
													preview={false}
													fallback="data:image/png;base64,"
													style={{ height: "200px" }}
												/>
											}
										>
											<Card.Meta
												title={recruit.employment_title}
												description={recruit.company_name}
											/>
										</Card>
									</Link>
								</Col>
							);
						})}
					</Row>
				</Col>
			</Row>
		</Content>
	);
};

export default RecuitCompanyPage;
